#include <stdio.h>

#define COUNT 8

void sorting(int *, int);
void printArr(int *ar, int *count);

int main(int argc, char const *argv[])
{
    int arr[COUNT] = {1312, 99, 42, 3, [5] = 9, 11, 44};
    printArr(arr, arr + COUNT);
    sorting(arr, COUNT);
    printArr(arr, arr + COUNT);
    return 0;
}

void sorting(int *ar, int count)
{
    int tem;
    for (int i = 8; i > 0; i--)
    {
        for (int j = 0; j < i; j++)
        {
            if (*(ar + j) > *(ar + j + 1))
            {
                tem = *(ar + j + 1);
                ar[1 + j] = ar[j];
                ar[j] = tem;
            }
        }
    }
}

void printArr(int *ar, int *count)
{
    while (ar < count)
    {
        printf("%d\t", *(ar++));
    }                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
    printf("\n");
}